<?phpclass TOROFILM_Activator{	public static function activate(){		update_option('thumbnail_size_w', 193);		update_option('thumbnail_size_h', 288);		$array = array( 			'wp_inactive_widgets' => array(), 			'sidebar-home'        => array('widget_list_movies_series-2'),			'sidebar-principal'   => array(),			'array_version'       => 3        );        $ar = array (            '2'            => array('title' => 'Movies And Series' , 'number' => 20),            '_multiwidget' => 1,        );        update_option( 'sidebars_widgets', $array );        update_option( 'widget_widget_list_movies_series', $ar );        $image = TOROFILM_DIR_URI . 'public/img/poster.jpg';	}}