<?php $fecha = get_post_meta( get_the_ID(), 'field_release_year', true );$fechas      = explode('-', $fecha);$tmdb       = get_post_meta( get_the_ID(), 'rating', true );if(!$tmdb){$tmdb = 0;} ?><li>		<article class="post dfx fcl movies">		<header class="entry-header">			<h2 class="entry-title"><?php the_title(); ?></h2>			<div class="entry-meta"><?php if($tmdb){ ?><span class="vote"><span>TMDB</span> <?php echo $tmdb; ?></span><?php } ?></div>		</header>		<div class="post-thumbnail or-1">			<figure><?php echo tr_theme_img(get_the_ID(), 'medium', get_the_title()); ?></figure>			<?php if($fechas[0]){ ?><span class="year"><?php echo $fechas[0]; ?></span><?php } ?> <?php  if ( 'movies' == get_post_type(get_the_ID()) ) { ?>				<span class="watch btn sm"><?php _e('View Movie', 'torofilm'); ?></span><?php } elseif( 'series' == get_post_type(get_the_ID()) ){ ?> <span class="watch btn sm"><?php _e('View Serie', 'torofilm'); ?></span><?php } ?>			<span class="play fa-play"></span>		</div>		<a href="<?php the_permalink(); ?>" class="lnk-blk"></a>	</article></li>