<?php if ( 'movies' == get_post_type(get_the_ID()) ) { $fecha = get_post_meta( get_the_ID(), 'field_release_year', true ); } elseif('series' == get_post_type(get_the_ID())){	$fecha = get_post_meta( get_the_ID(), 'field_date', true );}$fechas = explode('-', $fecha); $tmdb   = get_post_meta( get_the_ID(), 'rating', true );if(!$tmdb){$tmdb = 0;} ?><li>	<article class="post dfx alg-cr top">		<header class="entry-header fg1">			<h2 class="entry-title"><?php the_title(); ?></h2>			<div class="entry-meta"><?php if(get_post_meta( get_the_ID(), 'rating' )){ ?><span class="vote"><span>TMDB</span> <?php echo $tmdb; ?></span><?php } ?>				<?php if(get_post_meta( get_the_ID(), 'field_runtime')){ if ( 'movies' == get_post_type(get_the_ID()) ) { ?><span class="time"><?php echo get_post_meta( get_the_ID(), 'field_runtime', true ); ?></span><?php } elseif('series' == get_post_type(get_the_ID())){ ?><span class="time"><?php echo get_post_meta( get_the_ID(), 'field_runtime', true )[0]; ?> min.</span><?php } } ?> <?php if($fechas[0]){ ?> <span class="year"><?php echo $fechas[0]; ?></span><?php } ?>			</div>		</header>		<div class="post-thumbnail or-1">			<figure><?php echo tr_theme_img(get_the_ID(), 'thumbnail', get_the_title()); ?></figure>		</div>		<a href="<?php the_permalink(); ?>" class="lnk-blk"></a>		</article></li>